    // to add a story into the table
    public void addStory(Story story) {
        Log.d("addStory", story.toString());

        // get reference to writable DB
        SQLiteDatabase db = this.getWritableDatabase();

        ContentValues values = new ContentValues();
        values.put(STORY_TITLE, story.getTitle());
        values.put(STORY_CATEGORY, story.getCategory());
        values.put(STORY_CONTENT, story.getContent());
        values.put(STORY_NUMBER, story.getNumber());

        // insert to db
        db.insert(TABLE_NAME_STORY_LIST, null, values);

        // close connection
        db.close();
    }

    public Story getStory(int number) {
        SQLiteDatabase db = this.getReadableDatabase();

        Cursor cursor = db.query(TABLE_NAME_STORY_LIST, new String[] {_ID,
                        STORY_TITLE, STORY_CATEGORY, STORY_CONTENT, STORY_NUMBER}, STORY_NUMBER + "=?",
                new String[] { String.valueOf(number) }, null, null, null, null);
        if (cursor != null)
            cursor.moveToFirst();
        Story story =new Story(
                cursor.getString(1),
                cursor.getString(2),
                cursor.getString(3),
                Integer.parseInt(cursor.getString(4)));
        return story;
    }

    public User getUser(int id) {
        SQLiteDatabase db = this.getReadableDatabase();

        Cursor cursor = db.query(TABLE_NAME_USER_LIST, new String[] {_ID,
                        USER_NAME, USER_TITLE, USER_CONTENT, USER_HINT, USER_KEY, USER_NUMBER}, USER_NUMBER + "=?",
                new String[] { String.valueOf(id) }, null, null, null, null);
        if (cursor != null)
            cursor.moveToFirst();
        User user =new User(
                cursor.getString(1),
                cursor.getString(2),
                cursor.getString(3),
                cursor.getString(4),
                cursor.getString(5),
                cursor.getInt(6));
        return user;
    }


    @Override
    public void onUpgrade(SQLiteDatabase db, int oldVersion, int newVersion) {
        final String story = "DROP TABLE IF EXISTS " + TABLE_NAME_STORY_LIST;
        // Note: To upgrade table by delete/create table is not a good way.
        db.execSQL(story);
        this.onCreate(db);

    }

